import { useState } from 'react';
import './Header.scss';
import { Navbar } from 'react-bootstrap';
import useOnlineState from '../../hooks/useOnlineState';
import { ErrorToast } from '../ErrorToast/ErrorToast';
import { WalletStatus } from '../WalletStatus/WalletStatus';
import { ThemeSwitcher } from '../ThemeSwitcher/ThemeSwitcher';
import { NavMenu } from './NavMenu/NavMenu';
import { NetworkSelector } from '../NetworkSelector/NetworkSelector';
import { useWallet } from 'use-wallet';
import { isETH } from '../../utils/zunami';

function chainNameToTooltip(chainId: number) {
    if (chainId === 1 || !chainId) {
        return (
            <div>
                Please note. The contract{' '}
                <a
                    target="_blank"
                    rel="noreferrer"
                    href="https://github.com/ZunamiLab/ZunamiProtocol/tree/main/audit"
                >
                    has been audited
                </a>
                , <br />
                but it's still a beta version. Use it at your own risk
            </div>
        );
    } else {
        return (
            <div>
                Please note. This is the alpha version of the BSC cross-chain gateway. Use it at
                your own risk!
            </div>
        );
    }
}

export const Header = (): JSX.Element => {
    const logoVariant = document.body.classList.contains('dark') ? 'logo-dark.svg' : 'logo.svg';
    const [open, setOpen] = useState(false);
    const isOnline = useOnlineState();
    const { chainId } = useWallet();

    return (
        <Navbar expand="lg" className={'Header'}>
            <ErrorToast visible={!isOnline} />
            <div className="inner h-100">
                <NavMenu
                    onSelect={() => {
                        document.body.classList.remove('overflow');
                    }}
                />
                <NetworkSelector />
                <WalletStatus />
                <svg
                    width="2"
                    height="41"
                    viewBox="0 0 2 41"
                    fill="none"
                    xmlns="http://www.w3.org/2000/svg"
                    className="divider"
                >
                    <path d="M1 1L1 40" stroke="#F3EBD2" strokeLinecap="round" />
                </svg>
                <ThemeSwitcher />
                <div
                    className="nav-menu"
                    onClick={() => {
                        setOpen(!open);
                        document.getElementById('MobileSidebar').classList.toggle('active');
                        document.body.classList.toggle('overflow');
                    }}
                >
                    {open && (
                        <svg
                            width="21"
                            height="21"
                            viewBox="0 0 21 21"
                            fill="none"
                            xmlns="http://www.w3.org/2000/svg"
                        >
                            <path
                                d="M1 19.3848L19.3848 0.99999"
                                stroke="#F95A05"
                                strokeWidth="2"
                                strokeLinecap="round"
                            />
                            <path
                                d="M1 1L19.3848 19.3848"
                                stroke="#F95A05"
                                strokeWidth="2"
                                strokeLinecap="round"
                            />
                        </svg>
                    )}
                    {!open && (
                        <svg
                            width="35"
                            height="10"
                            viewBox="0 0 35 10"
                            fill="none"
                            xmlns="http://www.w3.org/2000/svg"
                        >
                            <path
                                d="M13.2793 9L34.0002 9"
                                stroke="#F95A05"
                                strokeWidth="2"
                                strokeLinecap="round"
                            />
                            <path
                                d="M1 1H34"
                                stroke="#F95A05"
                                strokeWidth="2"
                                strokeLinecap="round"
                            />
                        </svg>
                    )}
                </div>
            </div>
        </Navbar>
    );
};
